import { NavLink, Outlet, useSearchParams, useLocation } from "react-router-dom";
import { getBooks } from "../data/data";
const Books = () => {
  const books = getBooks();
  const [SearchParams, setSearchParams] = useSearchParams();
  const Location = useLocation();
  return (
    <div style={{ display: "flex" }}>
      <nav style={{ borderLeft: "solid 1px gray", padding: "1rem" }}>
        <span class="mr-5">
          <span id="search-area" class="search-area d-none">
            <i id="search-area-hide" class="fas fa-times pointer"></i>
           <input type="text" placeholder="جستجوی کتاب"
          value={SearchParams.get('filter') || ''}
          onChange={(event) => {
            let filter = event.target.value;
            if (filter) {
              setSearchParams({ filter })
            } else {
              setSearchParams({});
            }
          }} />
              <i class="fas fa-search pointer"></i>
          </span>
        </span>
        {books.filter((book) => {
          let filter = SearchParams.get("filter");
          if (!filter) return true;
          let name = book.name.toLowerCase();
          return name.startsWith(filter.toLowerCase());
        })
          .map((book) => (
            <NavLink
              style={({ isActive }) => {
                return {
                  display: "block",
                  margin: "1rem 0",
                  color: isActive ? "red" : " ",
                };
              }}
              to={`/books/${book.number} ${Location.search}`}
              key={book.number}
            >
              {book.name}
            </NavLink>
          ))}
      </nav>
      <Outlet />
    </div>
  );
};

export default Books;
