import { useParams,useLocation,useHref,useNavigate } from "react-router-dom";
import { deleteBook } from "../data/data";
import { getBook } from "../data/data";
const Book=()=>{
    const params=useParams();
    const Navigate=useNavigate();
    
    const book=getBook(parseInt(params.bookId));
    if (book){
        return(
            <main style={{padding:"1rem"}}>
                <h2>:قیمت {`${book.amount}تومان `}</h2>
                <p> {book.name}</p>
                <p>:تاریخ انتشار{book.due}</p>
                <p><button>حذف کتاب</button></p>
            </main>
        );
    }else{
        return(
           <main style={{padding:"1rem"}}>
            <p>همچین کتابی یافت نشد...</p>
        </main>  
        )
        }
    

};export default Book;
