import { useParams, useLocation, useNavigate } from "react-router-dom";
import { deleteBook } from "../data/data";
import { getBook } from "../data/data";
const Book = () => {
    const params = useParams();
    const Navigate = useNavigate();
    const Location = useLocation();
    const book = getBook(parseInt(params.bookId));
    if (book) {
        return (
            <main style={{ padding: "1rem" }}>
                <img src={book.photo} style={{width}} alt="" />
                <h3>قیمت:{`${book.amount}تومان`}</h3>

                <p>«{book.name} »</p>
                <p>نویسنده:{book.writer}</p>
                <p> موضوع کتاب:{book.sub}</p>
                <p>تاریخ انتشار:{book.due}</p>
                <p>معرفی کتاب:</p>
                <p>{book.desc}</p>
                <p><button onClick={() => {
                    deleteBook(book.number)
                    Navigate("/books" + Location.search)
                }} className="btn bg-danger active"
                ><i className="fa fa-trash"></i></button></p>
            </main>
        );
    } else {
        return (
            <main style={{ padding: "1rem" }}>
                <p style={{color:"orange"}}>همچین کتابی یافت نشد...</p>
            </main>
        )
    }


}; export default Book;
